import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Metal {
  id?: number;
  name: string;
  symbol: string;
}

@Injectable({
  providedIn: 'root'
})
export class MetalService {
  import { environment } from 'src/environments/environment';

private apiUrl = `${environment.apiBaseUrl}/metals`;

  constructor(private http: HttpClient) { }

  getAllMetals(): Observable<Metal[]> {
    return this.http.get<Metal[]>(this.apiUrl);
  }

  createMetal(metal: Metal): Observable<Metal> {
    return this.http.post<Metal>(this.apiUrl, metal);
  }

  updateMetal(id: number, metal: Metal): Observable<Metal> {
    return this.http.put<Metal>(`${this.apiUrl}/${id}`, metal);
  }

  deleteMetal(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }
}
