import { Component, OnInit } from '@angular/core';
import { MetalRateService, MetalRate } from '../metal-rate.service';
import { MetalService, Metal } from '../../metal/metal.service';
import { PurityService, Purity } from '../../purity/purity.service';
import { fadeInOut, listStagger } from '../../animations';

@Component({
  selector: 'app-metal-rate-list',
  templateUrl: './metal-rate-list.component.html',
  styleUrls: ['./metal-rate-list.component.css'],
  animations: [fadeInOut, listStagger]
})
export class MetalRateListComponent implements OnInit {
  metalRates: MetalRate[] = [];
  metals: Metal[] = [];
  purities: Purity[] = [];
  showFormFlag = false;
  selectedMetalRate: MetalRate = { 
    id: 0, 
    metalId: '', 
    purityId: 0, 
    rate: 0, 
    effectiveDate: new Date().toISOString().split('T')[0] 
  };
  loading = true;
  loadingMetals = true;
  loadingPurities = true;

  constructor(
    private metalRateService: MetalRateService,
    private metalService: MetalService,
    private purityService: PurityService
  ) { }

  ngOnInit(): void {
    this.loadData();
  }

  loadData(): void {
    this.loadMetalRates();
    this.loadMetals();
    this.loadPurities();
  }

  loadMetalRates(): void {
    this.loading = true;
    this.metalRateService.getAllMetalRates().subscribe({
      next: (rates: MetalRate[]) => {
        this.metalRates = rates;
        this.loading = false;
      },
      error: (err) => {
        console.error('Error loading metal rates:', err);
        this.loading = false;
        alert('Failed to load metal rates');
      }
    });
  }

  loadMetals(): void {
    this.loadingMetals = true;
    this.metalService.getAllMetals().subscribe({
      next: (metals: Metal[]) => {
        this.metals = metals;
        this.loadingMetals = false;
      },
      error: (err) => {
        console.error('Error loading metals:', err);
        this.loadingMetals = false;
        alert('Failed to load metals');
      }
    });
  }

  loadPurities(): void {
    this.loadingPurities = true;
    this.purityService.getAllpurity().subscribe({
      next: (purities: Purity[]) => {
        this.purities = purities;
        this.loadingPurities = false;
      },
      error: (err) => {
        console.error('Error loading purities:', err);
        this.loadingPurities = false;
        alert('Failed to load purities');
      }
    });
  }

  showForm(): void {
    this.selectedMetalRate = { 
      id: 0, 
      metalId: this.metals[0]?.id || '',  // Default to first metal if available
      purityId: this.purities[0]?.id || 0,  // Default to first purity if available
      rate: 0, 
      effectiveDate: new Date().toISOString().split('T')[0] 
    };
    this.showFormFlag = true;
    this.scrollToBottom();
  }

  editMetalRate(rate: MetalRate): void {
    this.selectedMetalRate = { ...rate };
    this.showFormFlag = true;
    this.scrollToBottom();
  }

  private scrollToBottom(): void {
    window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' });
  }

  deleteMetalRate(id: number): void {
    if (confirm('Are you sure you want to delete this metal rate?')) {
      this.metalRateService.deleteMetalRate(id).subscribe({
        next: () => {
          this.metalRates = this.metalRates.filter(r => r.id !== id);
        },
        error: (err) => {
          console.error('Error deleting metal rate:', err);
          alert('Failed to delete metal rate');
        }
      });
    }
  }

  handleFormSubmit(rate: MetalRate): void {
    const operation = rate.id ? 'update' : 'create';
    
    const observable = rate.id 
      ? this.metalRateService.updateMetalRate(rate.id, rate)
      : this.metalRateService.createMetalRate(rate);

    observable.subscribe({
      next: (savedRate) => {
        if (rate.id) {
          const index = this.metalRates.findIndex(r => r.id === savedRate.id);
          if (index > -1) this.metalRates[index] = savedRate;
        } else {
          this.metalRates = [...this.metalRates, savedRate];
        }
        this.hideForm();
      },
      error: (err) => {
        console.error(`Error ${operation}ing metal rate:`, err);
        alert(`Failed to ${operation} metal rate`);
      }
    });
  }

  hideForm(): void {
    this.showFormFlag = false;
  }

  getMetalName(metalId: string): string {
    const metal = this.metals.find(m => m.id === metalId);
    return metal?.name || 'Unknown Metal';
  }

  getMetalSymbol(metalId: string): string {
    const metal = this.metals.find(m => m.id === metalId);
    return metal?.symbol || '?';
  }

  getPurityValue(purityId: number): number {
    const purity = this.purities.find(p => p.id === purityId);
    return purity?.value || 0;
  }

  trackById(index: number, item: any): number {
    return item.id;
  }
}
