import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment';
import { ApiService } from '../shared/api.service';

export interface MetalRate {
  id?: string;
  metalId: string;
  purityId: string;
  rate: number;
  effectiveDate: string;
}

@Injectable({
  providedIn: 'root'
})
export class MetalRateService {
  

private apiUrl = `${environment.apiBaseUrl}/metalRates`;

  constructor(private api: HttpClient) { }

  getAllMetalRates(): Observable<MetalRate[]> {
    return this.api.get<MetalRate[]>(this.apiUrl);
  }

  createMetalRate(rate: MetalRate): Observable<MetalRate> {
    return this.api.post<MetalRate>(this.apiUrl, rate);
  }

  updateMetalRate(id: string, rate: MetalRate): Observable<MetalRate> {
    return this.api.put<MetalRate>(`${this.apiUrl}/${id}`, rate);
  }

  deleteMetalRate(id: string): Observable<void> {
    return this.api.delete<void>(`${this.apiUrl}/${id}`);
  }
}
