import { Component, OnInit } from '@angular/core';
import { User, UserService } from './user.service';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html'
})
export class UserListComponent implements OnInit {
  users: User[] = [];
  selectedUser: User | null = null;
  showForm = false;

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.fetchUsers();
  }

  fetchUsers() {
    this.userService.getUsers().subscribe(users => this.users = users);
  }

  editUser(user: User) {
    this.selectedUser = { ...user };
    this.showForm = true;
  }

  createNewUser() {
    this.selectedUser = { name: '', email: '' };
    this.showForm = true;
  }

  handleFormSubmit(user: User) {
    if (user.id) {
      this.userService.updateUser(user).subscribe(() => this.afterSave());
    } else {
      this.userService.createUser(user).subscribe(() => this.afterSave());
    }
  }

  afterSave() {
    this.showForm = false;
    this.selectedUser = null;
    this.fetchUsers();
  }

  deleteUser(id: string) {
    this.userService.deleteUser(id).subscribe(() => this.fetchUsers());
  }

  cancel() {
    this.showForm = false;
    this.selectedUser = null;
  }
}
