import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface MetalRate {
  id?: string;
  metalId: string;
  purityId: string;
  rate: number;
  effectiveDate: string;
  createdAt?: string;
}

@Injectable({
  providedIn: 'root'
})
export class MetalRateService {
  constructor(private http: HttpClient) {}

  getAllMetalRates(): Observable<MetalRate[]> {
    return this.http.get<MetalRate[]>('/api/metalRates'); // ✅ No filters
  }

  createMetalRate(rate: MetalRate): Observable<MetalRate> {
    return this.http.post<MetalRate>('/api/metalRates', rate);
  }

  updateMetalRate(id: string, rate: MetalRate): Observable<MetalRate> {
    return this.http.put<MetalRate>(`/api/metalRates/${id}`, rate);
  }

  deleteMetalRate(id: string): Observable<void> {
    return this.http.delete<void>(`/api/metalRates/${id}`);
  }
}
